mapper.delete:
DECLARE
BEGIN
  DELETE FROM TB_USER_WISH
  WHERE usr_id = #{userId,jdbcType=VARCHAR };

  DELETE FROM TB_USER
  WHERE usr_id = #{userId,jdbcType=VARCHAR };
  END;

mapper.update:
/*insertRefreshToken*/

UPDATE TB_USER
SET
    REFRESH =  #{map.refresh,jdbcType=VARCHAR }
WHERE
    USR_ID = #{map.userVo.userId, jdbcType=VARCHAR}



  UPDATE TB_SELLER
  SET
  REFRESH =  #{map.refresh,jdbcType=VARCHAR }
  WHERE
  SLLR_ID = #{map.userVo.userId, jdbcType=VARCHAR}

mapper.select:
/*selectRefreshToken*/ SELECT

  REFRESH
  FROM TB_USER
  WHERE
  USR_ID = #{userId}



  REFRESH
  FROM TB_SELLER
  WHERE
  SLLR_ID = #{userId}
