/*selectItemDetailInfo*/ SELECT A.PRDT_NO as itemId, A.PRDT_PRCE as price, A.PRDT_NM as name, A.PRDT_DESC as itemDetail, B.IMG_URL as image, C.LIKE_CNT as likeCnt, D.CMT_CNTT FROM TB_PRODUCT A LEFT JOIN TB_PRODUCT_IMAGE B ON A.PRDT_NO = B.PRDT_NO LEFT JOIN TB_PRODUCT_SOCIAL C ON B.PRDT_NO = C.PRDT_NO LEFT JOIN TB_PRODUCT_COMMENT D ON C.PRDT_NO = D.PRDT_NO WHERE A.PRDT_NO = #{pdNum, jdbcType=VARCHAR}
SELECT A.PRDT_NO as productId, A.CRT_DT as createDate, A.UPD_DT as updateDate, A.SLLR_ID as sellerId, A.PRDT_NM as name, A.PRDT_PRCE as price, A.PRDT_DESC as detail, B.IMG_URL as image FROM TB_PRODUCT A, TB_PRODUCT_IMAGE B WHERE A.PRDT_NO=B.PRDT_NO
/*selectMainPage*/ SELECT A.* FROM( SELECT MAX(A.PRDT_NO) as itemId, MAX(A.PRDT_PRCE) as price, MAX(A.PRDT_NM) as name, MAX(A.PRDT_DESC) as itemDetail, MAX(B.IMG_URL) as image, MAX(C.LIKE_CNT) as likeCnt FROM TB_PRODUCT A LEFT JOIN TB_PRODUCT_IMAGE B ON A.PRDT_NO = B.PRDT_NO LEFT JOIN TB_PRODUCT_SOCIAL C ON B.PRDT_NO = C.PRDT_NO GROUP BY A.PRDT_NO ORDER BY bookCnt ASC ) A WHERE ROWNUM <= 3
