mapper.select:

  /* selectProductCount */ SELECT
  COUNT(1)
FROM
  TB_PRODUCT A
WHERE
  A.SLLR_ID = #{sllrId, jdbcType=VARCHAR}

mapper.update:
/* modifyProduct */ UPDATE TB_PRODUCT SET
PRDT_PRCE = #{vo.prdtPrce} ,
PRDT_NM = #{vo.prdtNm} ,
PRDT_DESC = #{vo.prdtDesc} ,
UPD_DT = SYSDATE
WHERE SLLR_ID = #{vo.sllrId} AND PRDT_NO= #{vo.prdtNo}
